var expect = require('chai').expect;
var nationalities = require('./index');

describe('nationalities', function() {

  describe('all', function() {
    it('should be an array of value|label', function(){
      expect(nationalities.all).to.satisfy(isArrayOfOption);

      function isArrayOfOption(array) {
        return array.every(function(item) {
          return typeof item.value === 'string' && typeof item.label === 'string';
        })
      }
    });

    it('should contain Thai', function() {
      expect(nationalities.all).to.include({ value: 'thai', label: 'ไทย'});
    });
  });

  describe('random', function(){
    it('should return a random item from the nationalities.all', function(){
      var randomItem = nationalities.random();
      expect(nationalities.all).to.include(randomItem);
    });
  });

  describe('random 2', function(){
    it('should return a random item from the nationalities.all', function(){
      var randomItems = nationalities.random(3);
      // expect(nationalities.all).to.include(randomItems);
      expect(randomItems).to.have.length(3);
      randomItems.forEach(function(item) {
        expect(nationalities.all).to.include(item);
      });
    });
  })
});
